SELECT i_item_id ,i_item_desc ,i_current_price FROM item, inventory, date_dim, catalog_sales WHERE i_current_price between 58 AND 58 + 30 AND inv_item_sk = i_item_sk AND d_date_sk=inv_date_sk AND d_date between cast('2002-02-18' AS date) AND (cast('2002-02-18' AS date) + interval '60 days') AND i_manufact_id in (832,757,736,999) AND inv_quantity_on_hand between 100 AND 500 AND cs_item_sk = i_item_sk GROUP BY i_item_id,i_item_desc,i_current_price ORDER BY i_item_id LIMIT 100;
